import React from 'react'
import NewCategoryForm from './NewCategoryForm'
import NewGoalForm from './NewGoalForm'
import { useState } from 'react'
// import 'bulma/css/bulma.min.css';


export default function DashBoard(props) {

    const [goals, setGoals] = useState([])

    const addGoal = (goal) => {
        setGoals([goal])
    }

    // console.log(props)
    // console.log(goals)

    return (
        <div>
            <button className="button is-primary" onClick={props.logout}>Logout</button>
            <p>Hello {props.userInfo.name} remember "{props.userInfo.quote}"</p>
            <br></br>
            <NewCategoryForm userInfo={props.userInfo} addCategory={props.addCategory}/>
            {props.userInfo.categories.map((category, index) => (<p key={index}>{category.name} Duration {category.duration}</p>))}
            <br></br>
            <NewGoalForm userInfo={props.userInfo} addGoal={addGoal}/>
        </div>
    )
}
