import logo from './logo.svg';
import React, { Component } from 'react';
import 'bulma/css/bulma.min.css';
import { Route, Switch, withRouter } from 'react-router-dom';
import { useState, useEffect } from 'react';
import LogInForm from './components/LogInForm'
import SignUpForm from './components/SignUpForm';
import NavBar from './components/NavBar';
import DashBoard from './components/DashBoard'
import { responsiveFontSizes } from '@material-ui/core';

function App(props) {

  const [userInfo, setUserInfo] = useState({
    id: 0, 
    name: '',
    username: '',
    quote:'',
    token: "", 
    categories: [],
  })

  useEffect(() => {
    if(localStorage.token)
      fetch("http://localhost:3000/me", {
        headers: {
          "authorization" : localStorage.token
        }
      })
      .then(res => res.json())
      .then(handleResponse)
  },[]);

  // const [categories, setCategories] = useState([]
  // )

  const handleSignUp = (userInfo) => {
    console.log("Create account form has been submitted")
    console.log(userInfo)

    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify({
        name: userInfo.name,
        username: userInfo.username,
        password: userInfo.password, 
        quote: userInfo.quote
      })
    })
      .then(res => res.json())
      .then(handleResponse)
  }

  const handleResponse = (resp) => {
    console.log(resp)
    if(resp.token){
      setUserInfo({
        ...userInfo,
        username: resp.user.username,
        name: resp.user.name,
        quote: resp.user.quote,
        token: resp.token,
        categories: resp.user.categories
      })
      localStorage.token = resp.token
      props.history.push("/dashboard")
    } else {
      alert("Request error")
    }
  }

  const handleLogin = (userInfo) => {
    console.log(userInfo)
    console.log("Login form has been submitted")

    fetch("http://localhost:3000/login", {
      method: "POST",
      headers :{
        "content-type": "application/json"
      }, 
      body: JSON.stringify({
        username: userInfo.username,
        password: userInfo.password
      })
    })
      .then(res => res.json())
      .then(handleResponse)
  }

  const addCategory = (category) => {
    setUserInfo({
      ...userInfo,
      categories: [...userInfo.categories, category]
    })
  }

  console.log(userInfo)

  const handleLogout = () => {
    console.log("logging out")
    setUserInfo({
      id: 0, 
      name: '',
      username: '',
      quote:'',
      token: "", 
      categories: [],
    })
    localStorage.clear()
  }

  return (
    <div className="container">
      {/* Navigation bar */}

      <Switch>
        <Route exact path="/">
          <div className="level-item has-text-centered">
            <LogInForm login={handleLogin} logout={handleLogout}/>
          </div>
        </Route>

        <Route exact path="/signup">
        <div className="level-item has-text-centered">
          <SignUpForm signUp={handleSignUp}></SignUpForm>
          </div>
        </Route>

        <Route exact path="/dashboard">
        <div className="level-item has-text-centered">
          <DashBoard userInfo={userInfo} addCategory={addCategory}></DashBoard>
          </div>
        </Route>
      </Switch>
      </div>
  );
}

export default withRouter(App)
