import React from 'react'
import { Link } from 'react-router-dom'

export default function Categories(props) {

    console.log(props)

    const renderCategories = props.categoryInfo.categories.map((category) => (
        <tr>
            <td>{category.name}</td>
            <td>{category.duration}</td>
            <td><Link to='/editcategory'><button>Edit</button></Link></td>
            <td><Link><button onClick={handleDelete}>Delete</button></Link></td>
        </tr>
    ))

    const handleDelete = (e) => {

        fetch(`http://localhost:3000/categories/${props.id}`, {
            method: "DELETE",
            headers: {
                "authorization" : props.userInfo.token
            }
        })
            .then(res => res.json())
            .then(res => {
                if (res.id) {
                    props.deleteCategory(res)
                }
            }) 
        }


    return (
       <>
       {renderCategories}
       </>
    )
}
