// import logo from './logo.svg';
// import React, { Component } from 'react';
import 'bulma/css/bulma.min.css';
import { Route, Switch, withRouter } from 'react-router-dom';
import { useState, useEffect } from 'react';
import LogInForm from './components/LogInForm'
import SignUpForm from './components/SignUpForm';
import DashBoard from './components/DashBoard'
// import { responsiveFontSizes } from '@material-ui/core';

function App(props) {

  const [userInfo, setUserInfo] = useState({
    id: 0, 
    name: '',
    username: '',
    quote:'',
    token: "", 
    categories: [],
  })

  console.log(userInfo.categories)
  useEffect(() => {
    if(localStorage.token)
      fetch("http://localhost:3000/me", {
        headers: {
          "authorization" : localStorage.token
        }
      })
      .then(res => res.json())
      .then(handleResponse)
  },[]);

  // const [categories, setCategories] = useState([]
  // )

  const handleSignUp = (userInfo) => {
    console.log("Create account form has been submitted")

    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify({
        name: userInfo.name,
        username: userInfo.username,
        password: userInfo.password, 
        quote: userInfo.quote
      })
    })
      .then(res => res.json())
      .then(handleResponse)
  }

  const handleResponse = (resp) => {
    if(resp.token){
      setUserInfo({
        ...userInfo,
        username: resp.user.username,
        name: resp.user.name,
        quote: resp.user.quote,
        token: resp.token,
        categories: resp.user.categories
      })
      localStorage.token = resp.token
      props.history.push("/dashboard")
    } else {
      alert("Request error")
    }
  }

  const handleLogin = (userInfo) => {
    console.log("Login form has been submitted")

    fetch("http://localhost:3000/login", {
      method: "POST",
      headers :{
        "content-type": "application/json"
      }, 
      body: JSON.stringify({
        username: userInfo.username,
        password: userInfo.password
      })
    })
      .then(res => res.json())
      .then(handleResponse)
  }

  const addCategory = (category) => {
    setUserInfo({
      ...userInfo,
      categories: [...userInfo.categories, category]
    })
  }

  const addGoal = (goal) => {

    console.log(goal)

    let findCategoryForGoal = userInfo.categories.find(category => {
      return category.id === goal.category.id
    })

    let newArrOfGoals = findCategoryForGoal.categories.filter(goalObj => {
      return goalObj.id !== goal.id
    })

    let copyOfCategory = {
      ...findCategoryForGoal, 
      goals: newArrOfGoals
    }

    let copyOfAllCategories = userInfo.categories.map(categories => {
      if(categories.id === copyOfCategory.id) {
        return copyOfCategory
      } else {
        return categories
      }
    })

    setUserInfo({
      categories: copyOfAllCategories
    })
  }

  const handleLogout = () => {
    console.log("logging out")
    setUserInfo({
      id: 0, 
      name: '',
      username: '',
      quote:'',
      token: "", 
      categories: [],
    })
    localStorage.clear()
    props.history.push("/")
  }

  return (
    <div className="container">
      {/* Navigation bar */}

      <Switch>
        <Route exact path="/">
          <section className="hero is-fullheight has-background-white">
            <div className="hero-body">
              <div className="container">
                <div className="level-item has-text-centered">
                <LogInForm login={handleLogin}/>
                </div>
              </div>
            </div>
          </section>
        </Route>

        <Route exact path="/signup">
        <section className="hero is-fullheight has-background-white">
            <div className="hero-body">
              <div className="container">
                <div className="level-item has-text-centered">
                  <SignUpForm signUp={handleSignUp}></SignUpForm>
                </div>
              </div>
            </div>
          </section>
        </Route>

        <Route exact path="/dashboard">
            <DashBoard userInfo={userInfo} addCategory={addCategory} addGoal={addGoal} logout={handleLogout}></DashBoard>
        </Route>
      </Switch>
      </div>
  );
}

export default withRouter(App)
