import logo from './logo.svg';
import React, { Component } from 'react';
import 'bulma/css/bulma.min.css';
import { Route, Switch, withRouter } from 'react-router-dom';
import { useState } from 'react';
import LogInForm from './components/LogInForm'
import SignUpForm from './components/SignUpForm';
import NavBar from './components/NavBar';
import DashBoard from './components/DashBoard'

function App(props) {

  const [userInfo, setUserInfo] = useState({
    id: 0, 
    name: '',
    username: '',
    quote:''
  })

  const handleSignUp = (userInfo) => {
    console.log("Create account form has been submitted")
    console.log(userInfo)

    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify({
        name: userInfo.name,
        username: userInfo.username,
        password: userInfo.password, 
        quote: userInfo.quote
      })
    })
      .then(res => res.json())
      .then(handleResponse)
  }

  const handleResponse = (resp) => {
    console.log(resp)
    if(resp.token){
      setUserInfo({
        id: resp.user.id,
        username: resp.user.username,
        name: resp.user.name,
        quote: resp.user.quote
      })
      alert("Account created or logged in successfully!")
      props.history.push("/dashboard")
    } else {
      alert("Request error")
    }
  }

  const handleLogin = (userInfo) => {
    console.log(userInfo)

    fetch("http://localhost:3000/login", {
      method: "POST",
      headers :{
        "Content-type": "application/json"
      }, 
      body: JSON.stringify({
        username: userInfo.username,
        password: userInfo.password
      })
    })
      .then(res => res.json())
      .then(handleResponse)
  }

  const renderDashBoard = (routerProps) => {
    return <DashBoard/>
  }

  return (
    <div className="container">
      {/* Navigation bar */}

      <Switch>
        <Route exact path="/login">
          <div className="level-item has-text-centered">
            <LogInForm login={handleLogin}/>
          </div>
        </Route>

        <Route exact path="/signup">
        <div className="level-item has-text-centered">
          <SignUpForm signUp={handleSignUp}></SignUpForm>
          </div>
        </Route>

        <Route exact path="/dashboard">
        <div className="level-item has-text-centered">
          <DashBoard></DashBoard>
          </div>
        </Route>
      </Switch>
      </div>
  );
}

export default withRouter(App)
