import React from 'react'
import Categories from './Categories'
import { Link } from 'react-router-dom'

export default function CategoryContainer({userInfo, categories, deleteCategory}) {



    // let arrCategories = props.categories.map((categories) => {
    //     return <Categories key={categories.id} name={categories.name} duration={categories.duration}/>
    // })

    const handleDelete = (e) => {

        fetch(`http://localhost:3000/categories/${category.id}`, {
            method: "DELETE",
            headers: {
                "authorization" : userInfo.token
            }
        })
            .then(res => res.json())
            .then(res => {
                if (res.id) {
                    deleteCategory(res)
                }
            }) 
        }


    const renderRows = categories.map((category) => (
        <tbody>
            <td>{category.name}</td>
            <td> {category.duration} Hours</td>
            <td><Link to='/editcategory'><button>Edit</button></Link></td>
            <td><button onClick={handleDelete}>Delete</button></td>

        </tbody>
        ))


    return (
        <div>
            <table className="table">
            <thead>
                    <tr>
                        <th>Categories</th>
                    </tr>
                </thead>

                <tbody>
                    {renderRows}
                </tbody>
            </table>
        </div>
    )
}
