import React from 'react'
import { useState } from 'react'

export default function NewTaskForm(props) {

    const [taskInfo, setNewTaskInfo] = useState({
        name: '', 
        description: '', 
        priority: '', 
        difficulty: '', 
        start_date: '', 
        end_date: '',
        duration: '',
        category: '',
        goal: '',
        status_complete: false
    })

    let findGoalArr = props.categories.find(({category}) => category.id === taskInfo.category)

    console.log(props.categories)
    console.log(taskInfo)
    console.log(taskInfo.category)
    console.log(findGoalArr)


    const handleChange = (e) => {
        setNewTaskInfo({
            ...taskInfo,
            [e.target.name]: e.target.value
        }) 
    }

    // const handleSubmit = (e) => { 
    //     e.preventDefault()
    //     fetch("http://localhost:3000/createtask", {
    //         method: "POST", 
    //         headers: {
    //             "content-type": "application/json",
    //             "authorization": props.userInfo.token
    //             }, 
    //             body: JSON.stringify({
    //                 name: taskInfo.name,
    //                 duration: taskInfo.duration
    //             })
    //         })
    //             .then(res => res.json())
    //             .then((res) => 
    //                 (console.log(res))
    //             )
    // }

    return (
        <div>
            <form>
                <label htmlFor="name">Name</label>
                    <input onChange={handleChange} value={taskInfo.name} id="text" type="text" name="name"/>
                <label htmlFor="description">Description</label>
                    <input onChange={handleChange} value={taskInfo.description} id="text" type="text" name="description"/>
                <label htmlFor="priority">Priority</label>
                    <select onChange={handleChange} value={taskInfo.priority} type="category" name="priority">
                        <option value="" disabled selected hidden>Select</option>
                        <option>Must</option>
                        <option>Should</option>
                        <option>Want</option>
                    </select>
                <label htmlFor="difficulty">Difficulty</label>
                    <select onChange={handleChange} value={taskInfo.difficulty} 
                    type="category" name="difficulty">
                        <option value="" disabled selected hidden>Select</option>
                        <option>Worst</option>
                        <option>Not Bad</option>
                        <option>Easy</option>
                    </select>
                <label htmlFor="start_date">Start Date</label>
                    <input onChange={handleChange} value={taskInfo.start_date} type="date" name="start_date"/>
                <label htmlFor="end_date">End Date</label>
                    <input onChange={handleChange} value={taskInfo.end_date} type="date" name="end_date"/>
                <label htmlFor="duration">Duration</label>
                    <input onChange={handleChange} value={taskInfo.duration} id="text" type="text" name="duration"/>
                <label htmlFor="category">Category</label>
                    <select onChange={handleChange} value={taskInfo.category} type="category"name="category">
                        <option value="" disabled selected hidden>Select</option>
                        {props.categories.map((category, index) => (
                            <option key={category.id} value={category.id}>{category.name}</option>
                        ))}
                    </select>
                <label htmlFor="goal">Goal</label>
                    <select onChange={handleChange} value={taskInfo.goal} type="category"name="goal">
                        <option value="" disabled selected hidden>Select</option>
                        {/* {props.categories.goals.map((goals, index) => (
                            <option key={goals.id} value={goals.id}>{goals.name}</option>
                        ))}
                         */}

                    </select>
                <label htmlFor="status_complete">Complete</label>
                    <input onChange={handleChange} value={taskInfo.status_complete} type="checkbox" name="status_complete"/>
                <button onChange={handleChange} value="submit" type="button">Create Task</button>
            </form>
        </div>
    )
}
