import React from 'react'
import Categories from './Categories'
import { Link } from 'react-router-dom'

export default function CategoryContainer(props) {

    console.log(props)

    // let arrCategories = props.categories.map((categories) => {
    //     return <Categories key={categories.id} name={categories.name} duration={categories.duration}/>
    // })

    // const handleDelete = (e) => {
    //     fetch(`http://localhost:3000/categories/${props.categories.id}`, {
    //         method: "DELETE",
    //         headers: props.

    //     })

    // }

    const renderRows = props.categories.map((category) => (
        <tbody>
            <td>{category.name}</td>
            <td> {category.duration} Hours</td>
            <td><button>Edit</button></td>
            <td><Link to='/editcategory'><button>Delete</button></Link></td>
        </tbody>
        ))


    return (
        <div>
            <table className="table">
            <thead>
                    <tr>
                        <th>Categories</th>
                    </tr>
                </thead>

                <tbody>
                    {renderRows}
                </tbody>
            </table>
        </div>
    )
}
