import logo from './logo.svg';
import React, { Component } from 'react';
import 'bulma/css/bulma.min.css';
import { Route, Switch, withRouter } from 'react-router-dom';
import { useState } from 'react';
import LogInForm from './components/LogInForm'
import SignUpForm from './components/SignUpForm';
import NavBar from './components/NavBar';
import Home from './components/Home';
import DashBoard from './components/DashBoard'

function App(props) {

  const [userInfo, setUserInfo] = useState({
    id: 0, 
    name: '',
    username: '',
    quote:''
  })

  const handleSignUp = (userInfo) => {
    console.log("Create account form has been submitted")
    console.log(userInfo)

    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify({
        name: userInfo.name,
        username: userInfo.username,
        password: userInfo.password, 
        quote: userInfo.quote
      })
    })
      .then(res => res.json())
      .then(handleSignUpResponse)
  }

  const handleSignUpResponse = (resp) => {
    console.log(resp)
    if(resp.id){
      setUserInfo({
        id: resp.id,
        username: resp.username,
        name: resp.name,
        quote: resp.quote
      })
      alert("Accout created!")
    } else {
      alert("Error creating account")
    }
  }

  const handleLogin = (userInfo) => {
    const {username, password} = loginInfo
    const loginInfo = {username: username, password: password}
    fetch('"http://localhost:3000/login"', {
        method: 'POST',
        headers: {"Content-type":"application/json"},
        body: JSON.stringify(loginInfo)
    })
    .then(res => res.json())
    .then(userInfo => {props.login(userInfo)
        setUserInfo({
            username: '',
            password: ''
        })
    })
}

  return (
    <div className="container">
      {/* Navigation bar */}

      <Route path="/" exact component={Home} />

      <Route exact path="/login">
        <div className="level-item has-text-centered">
          <LogInForm login={handleLogin}/>
        </div>
      </Route>

      <Route exact path="/signup">
      <div className="level-item has-text-centered">
        <SignUpForm signUp={handleSignUp}></SignUpForm>
        </div>
      </Route>

      <Route exact path="/dashboard">
      <div className="level-item has-text-centered">
        <DashBoard></DashBoard>
        </div>
      </Route>
      </div>
  );
}

export default App;
