import React from 'react'
import { useState } from 'react'

export default function NewCategoryForm() {

    const [newCategoryInfo, setNewCategoryInfo] = useState({
        name: '', 
        duration: ''
    })

    const handleChange = (e) => {
        setNewCategoryInfo({
            ...newCategoryInfo,
            [e.target.name]: e.target.value
        }) 
    }

    const handleSubmit = (e) => {
        e.preventDefaut()
        console.log(newCategoryInfo)
    }


    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name</label>
            <input type="text" id="name" name="name" value={newCategoryInfo.name} onChange={handleChange}/>
            <label htmlFor="start_date">Duration</label>
            <input type="date" id="start_date" name="start_date" value={newCategoryInfo.start_date} onChange={handleChange}/>
            <label htmlFor="end_date">End Date</label>
            <input type="date" id="end_date" name="end_date" value={newCategoryInfo.end_date} onChange={handleChange}/>
            <button type="submit" value="Create">Create Category</button>
        </form>
    )
}
