import React from 'react'
import { Link } from 'react-router-dom'

export default function Categories(props) {

    const categoryIdInfo = [categoryId, setCategoryId] = useState(0)

    console.log(categoryId)

    const handleDelete = (e) => {

        fetch(`http://localhost:3000/categories/${categoryId}`, {
            method: "DELETE",
            headers: {
                "authorization" : props.categoryInfo.userInfo.token
            }
        })
            .then(res => res.json())
            .then(res => {
                if (res.id) {
                    props.deleteCategory(res)
                }
            }) 
        }

    const renderCategories = props.categoryInfo.categories.map((category) => (
        <tr>
            <td>{category.name}</td>
            <td>{category.duration}</td>
            <td><Link to='/editcategory'><button>Edit</button></Link></td>
            <td><Link><button onClick={handleDelete} value={category.id}>Delete</button></Link></td>
        </tr>
    ))


    return (
       <>
       {renderCategories}
       </>
    )
}
