import { constants } from 'fs'
import React from 'react'
import { useState } from 'react'

export default function NewTaskForm(props) {

    const [taskInfo, setNewTaskInfo] = useState({
        name: '', 
        description: '', 
        priority: '', 
        difficulty: '', 
        start_date: '', 
        end_date: '',
        duration: '',
        category: '',
        goal: '',
        status_complete: false
    })

    const handleChange = (e) => {
        setNewTaskInfo({
            ...taskInfo,
            [e.target.name]: e.target.value
        }) 
    }

    const handleSubmit = (e) => { 
        e.preventDefault()
        fetch("http://localhost:3000/createtask", {
            method: "POST", 
            headers: {
                "content-type": "application/json",
                "authorization": props.userInfo.token
                }, 
                body: JSON.stringify({
                    name: taskInfo.name,
                    duration: taskInfo.duration
                })
            })
                .then(res => res.json())
                .then((res) => 
                    (console.log(res))
                )
    }


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input onChange={handleChange} value={taskInfo.name} id="text" type="text" name="name"></input>
                <input onChange={handleChange} value={taskInfo.description} id="text" type="text" name="description"></input>
                <input onChange={handleChange} value={taskInfo.priority} type="category" name="priority"></input> 
                <input onChange={handleChange} value={taskInfo.difficulty} type="category" name="difficulty"></input>
                <input onChange={handleChange} value={taskInfo.start_date} type="date" name="start_date"></input>
                <input onChange={handleChange} value={taskInfo.end_date} type="date" name="end_date"></input>
                <input onChange={handleChange} value={taskInfo.duration} id="text" type="text" name="duration"></input>
                <input onChange={handleChange} value={taskInfo.category} type="category"name="category"></input>
                <input onChange={handleChange} value={taskInfo.goal} type="category"name="goal"></input>
                <input onChange={handleChange} value={taskInfo.status_complete} type="checkbox" name="status_complete"></input>
                <input onChange={handleChange} value="submit" type="button">Create Task</input>
            </form>
        </div>
    )
}
